#include "ros/ros.h"
#include "std_msgs/String.h"

int main(int argc,char *argv[])
{
    //2.初始化 ROS 节点:命名(唯一)
    // 参数1和参数2 后期为节点传值会使用
    // 参数3 是节点名称，是一个标识符，需要保证运行后，在 ROS 网络拓扑中唯一
    ros::init(argc,argv,"erGouzhi");
    //3.实例化 ROS 句柄  该类封装了 ROS 中的一些常用功能
    ros::NodeHandle nh;
    //4.实例化 发布者 对象
    //泛型: 发布的消息类型
    //参数1: 要发布到的话题
    //参数2: 队列中最大保存的消息数，超出此阀值时，先进的先销毁(时间早的先销毁)
    ros::Publisher pub_erGouzhi=nh.advertise("demo_service_topic",10);
    //实例化一个String 数据 用于存放消息
    std_msgs::String demo_service_msg;
    while(ros::ok())  //节点不死
    {
        demo_service_msg.data="hello";
        pub_erGouzhi.publish(demo_service_msg);
    }
    return 0;
}

